mapper.select:
select a.post_id postId, a.title title, a.description description, b.username username, b.avatar avatar, c.school_name schoolName from tb_post a left join tb_user b on a.user_id = b.user_id left join tb_school c on a.school_id = c.school_id where a.is_delete = 0
and a.user_id = #{postQueryConditions.userId}
and a.school_id = #{postQueryConditions.schoolId}
and a.title like "%"#{postQueryConditions.title}"%"
and a.content like "%"#{postQueryConditions.content}"%"

mapper.update:
update tb_post set title = #{post.title},description=#{post.description},content_mkd=#{post.contentMkd}
,content_html=#{post.contentHtml}
where post_id = #{post.postId}

mapper.select:
select a.comment_id commentId, a.user_id userId, a.content content, a.create_date createDate, b.username username, b.avatar avatar from tb_comment a left join tb_user b on a.user_id = b.user_id where a.comment_pid = 0 and a.post_id = #{postId}
order by a.create_date desc
order by a.create_date desc
